mapper.insert:
INSERT INTO UserInfo (userId
                    , userPw
                    , userName
                    , userGender
                    , userBirth)
VALUES (#{userId}
      , #{userPw}
      , #{userName}
      , #{userGender}
      , #{userBirth})

INSERT INTO UserDetailInfo (userId
                          , prop
                          , value)
     VALUES (#{userId}
           , #{prop}
           , #{value})

mapper.select:
SELECT *
  FROM UserInfo
 WHERE userId = #{userId}
   AND userPw = #{userPw}

mapper.update:
UPDATE UserInfo
   SET userId=#{userId}
     , userPw=#{userPw}
     , userName=#{userName}
     , userGender=#{userGender}
     , userBirth=#{userBirth}
 WHERE userId=#{userId}

UPDATE UserDetailInfo
   SET userId=#{userId}
     , value=#{value}
WHERE userId=#{userId} AND prop=#{prop}

UPDATE UserBook
   SET userId=#{userId}
     , value=#{value}
 WHERE userId=#{userId} AND bookNum=#{bookNum}

mapper.insert:

    SELECT MAX(bookNum) + 1
    FROM UserBook
    WHERE userId = #{userId}

INSERT INTO UserBook (userId
                    , bookNum
                    , value)
     VALUES (#{userId}
           , #{bookNum}
           , #{value})
